package com.mymoviedbapi;

import org.springframework.boot.ApplicationRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.annotation.Bean;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RestController;

import java.util.List;

@SpringBootApplication
public class MyMovieDbApiApplication {

    public static void main(String[] args) {
        SpringApplication.run(MyMovieDbApiApplication.class, args);
    }

    @Bean
    ApplicationRunner applicationRunner(MovieRepository movieRepository) {
        return args -> {
            movieRepository.save(
                    new Movie(
                    "Mroczny Rycerz Powstaje",
                            "Christopher Nolan",
                            "Akcja / Sci-Fi",
                            2008,
                            "data:image/jpeg;base64,",
                            "Batman, z pomocą porucznika Gordona oraz prokuratora Harveya Denta, występuje przeciwko przerażającemu i nieobliczalnemu Jokerowi, który chce pogrążyć Gotham City w chaosie."
                    ));
            movieRepository.save(
                    new Movie(
                            "GWIEZDNE WOJNY: CZĘŚĆ I - MROCZNE WIDMO",
                            "George Lucas",
                            "Przygodowy / Sci-Fi",
                            1999,
                            "data:image/jpeg;base64,",
                            "Dwaj rycerze Jedi wyruszają z misją ocalenia planety Naboo przed inwazją wojsk Federacji Handlowej. Trafiają na pustynny glob, gdzie pomoże im mały Anakin Skywalker."
                    ));
        };
    }

}

@RestController
class HelloController {
    private final MovieRepository movieRepository;

    public HelloController(MovieRepository movieRepository) {
        this.movieRepository = movieRepository;
    }

    @GetMapping("/")
    String hello() {
        return "Welcome to the MyMovieDB API";
    }

    @GetMapping("/movies")
    String movies(){

    }
}
